// src/components/Dashboard.tsx
import React from 'react';
import KPIChart from './KPIChart';

// Mock data extracted from JSON files
const lisinoprilPriceHistory = {
  dates: ['2023-01-01', '2023-02-01', '2023-03-01'],
  prices: [5.99, 6.09, 6.19] // Price in EUR for Zestril
};

const hospitalizationData = {
  months: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug'],
  values: [1500, 1600, 1700, 1800, 1900, 2000, 2100, 2200] // Monthly hospitalizations for Hôpital Saint-Jean
};

const Dashboard: React.FC = () => {
  return (
    <div className="container mx-auto p-8">
      <h1 className="text-3xl font-bold mb-8 text-center">KPI Dashboard</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Line chart for medication price history */}
        <KPIChart 
          title="Lisinopril (Zestril) Price History" 
          data={lisinoprilPriceHistory.prices} 
          labels={lisinoprilPriceHistory.dates} 
        />

        {/* Line chart for hospitalizations */}
        <KPIChart 
          title="Monthly Hospitalizations at Hôpital Saint-Jean" 
          data={hospitalizationData.values} 
          labels={hospitalizationData.months} 
        />
      </div>
    </div>
  );
};

export default Dashboard;
